import { CREATE, FETCH_ALL } from '../actions/actionTypes';
export default ((state = [], action) => {
  //where action constains the payload/data thats going to the Store
  switch (action.type) {
    case CREATE:
      return [...state, action.payload];

    case FETCH_ALL:
      return action.payload;

    default:
      return state;
  }
});
