//Regular imports
import React from "react";
import { useState } from "react";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import Grid from "@material-ui/core/Grid";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
//Actions

//Components
import First_Section_List from "./Sections/First/First_Section_List";
//Styles
import makeStyles from "../../../styles/List";

const List = ({ item }) => {
	const styles = makeStyles();
	const [count, setCount] = useState(0);

	return (
		<div>
			<Grid className={styles.root} container spacing={1}>
				<Grid item xs={2} className={styles.itemImageContainer}>
					<First_Section_List />
				</Grid>
				<Grid item xs={5} className={styles.details}>
					<Typography className={styles.itemName}>{item.name}</Typography>
					<Typography className={styles.itemType}>Item Type</Typography>
					<Typography className={styles.itemLocation}>Item location</Typography>
				</Grid>
				{/* ============================================ */}
				<Grid container className={styles.info}>
					<Grid item className={styles.moreIcon}>
						{/* Three dots icon */}
						<Button className={"random"}>
							<MoreHorizIcon />
						</Button>
						{/* Drawer */}
					</Grid>
					<Grid item>
						{" "}
						{/* Count GroupButtons */}
						<ButtonGroup>
							<Button
								className={styles.minus}
								onClick={() => (count > 0 ? setCount(count - 1) : count)}
							>
								-
							</Button>
							<Button className={styles.count}>{count}</Button>
							<Button
								className={styles.plus}
								onClick={() => setCount(count + 1)}
							>
								+
							</Button>
						</ButtonGroup>
					</Grid>
				</Grid>
			</Grid>
		</div>
	);
};

export default List;
