import {React, useEffect} from 'react'

import { useGoogleLogin } from 'react-google-login'
import { GoogleLogin} from 'react-google-login'

import { useHistory } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import Button from '@material-ui/core/Button'
//Local components
import { send_Google_User_info } from '../../actions/actions'

const Login = () => {
    const dispatch = useDispatch()
    const history = useHistory()
    const client_id = '534704394140-vgqdcmbmel4gn1bfa7g3hd6h70qm5c6m.apps.googleusercontent.com'

    //when Login works
    const onSuccess = (response) => {
        console.log(`Login Success: currentUser ${response.profileObj}`)
        console.log('Loaded: ' + loaded)
        dispatch(send_Google_User_info(response))
        history.push('/home')
    }
    //When login is a failute
    const onFailure = (response) => {
        console.log('Error: ' + response)
    }

    const { signIn, loaded } = useGoogleLogin({
        onSuccess: onSuccess,
        clientId: client_id,
        isSignedIn: true,
        onFailure: onFailure,
    })
    //=================================

    useEffect(()=>{
        console.log('From login')
    },[])

    return (
        <>
            <GoogleLogin
                clientId={client_id}
                buttonText="Login"
                onSuccess={onSuccess}
                onFailure={onFailure}
                cookiePolicy={'single_host_origin'}
                isSignedIn={true}
            />
            {/* <Button onClick={signIn} className='login_button'>
                Login
            </Button>
         */}
        </>
    )
}

export default Login
