import axios from 'axios'

const url = 'http://localhost:5050/users'   //first run json server,
                                                    //run ngrok http 5050, 
                                                    //change the url

export const getLists = (id) =>  axios.get(`${url}/${id}`)
export const createList = (newList, id) => axios.post(`${url}/${id}/lists`, newList)
