import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
//Local imports
import Add_Item from "./Add_Item/Add_item";
import Confirm from "./Confirm/Confirm";
import List from "./List/Item";

const Edit_List = () => {
	const items = useSelector((state) =>
		state.lists.map((item) => {
			item.isChecked = false;
			return item;
		})
	);
	const [new_Items, set_new_Item] = useState(
		useSelector((state) =>
			state.lists.map((item) => {
				item.isChecked = false;
				return item;
			})
		)
	);
	/* const [new_Items, set_new_Item] = useState(
		items.map((item) => {
			item.isChecked = false;
			return item;
		})
	); */

	const handleDelete = () => {
		set_new_Item(new_Items.filter((item) => item.isChecked === false));
	};

	useEffect(() => {
		console.log("FROM EDIT LISTS: " + JSON.stringify(new_Items));
	}, [new_Items]);

	return (
		<div>
			<div>
				<Add_Item />
			</div>
			<div>
				{new_Items.map((item) => (
					<List item={item} set_new_Item={set_new_Item} items={items} />
				))}
			</div>
			<div>
				<button onClick={handleDelete}>Delete</button>
			</div>
			<div>
				<Confirm set_new_Item={set_new_Item} />
			</div>
		</div>
	);
};

export default Edit_List;
