import axios from "axios";

const url = "http://localhost:5050/users"; //first run json server,
//run ngrok http 5050,
//change the url

export const getLists = (user_id) => axios.get(`${url}/${user_id}/lists`);
export const createList = (newList, user_id) =>
	axios.post(`${url}/${user_id}/lists`, newList);
export const fetch_items = (user_id, list_Id) =>
	axios.post(`${url}/users/${user_id}/listId?=${list_Id}`);
