import React, { useState, useEffect, useReducer } from "react";
import Button from "@material-ui/core/Button";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
//Local Imports
import { update_Item } from "../../../../../api/api";
//Styles
import makeStyles from "./Third_Section_List_styles";

const reducer = (state, action) => {
	switch (action.type) {
		case "set-count":
			return { count: state.payload };
		case "increment":
			return { count: state.count + 1 };
		case "decrement":
			return { count: state.count - 1 };
	}
};

const Third_Section_List = ({ setting_boolean, set_Setting_bolean, item }) => {
	const item_count = item.count;
	const styles = makeStyles();
	const [state, dispatch] = useReducer(reducer, { count: 1 });
	const [count, setCount] = useState(1);

	console.log(count);
	console.log(item_count);
	useEffect(async () => {
		// update count in JSON server everytime count changes
		try {
			const updated_item = {
				listId: item.listId,
				userId: item.userId,
				name: item.name,
				count: state.toString(),
			};
			await update_Item(item.id, updated_item);
		} catch (error) {
			console.log(error.message);
		}
	}, [count]);

	const handle_threeDots_button = () => {
		set_Setting_bolean(!setting_boolean);
	};

	return (
		<div>
			<div>
				{/* Three Dots button (Options) */}
				<Button className={styles.three_dots} onClick={handle_threeDots_button}>
					<MoreHorizIcon />
				</Button>
			</div>
			<div>
				{/* Item Count Buttons */}
				<ButtonGroup className={styles.group_button}>
					<Button className={styles.minus} onClick={() => setCount(count - 1)}>
						-
					</Button>
					<Button className={styles.count}>{item.count}</Button>
					<Button
						className={styles.plus}
						onClick={() => {
							console.log(`Before adding: ${count}`);
							setCount(count + 1);
							console.log(`After adding: ${count}`);
						}}
					>
						+
					</Button>
				</ButtonGroup>
			</div>
		</div>
	);
};

export default Third_Section_List;
