import React, { useState, useEffect } from "react";
import { Typography } from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
//Local Imports
import { update_Item } from "../../../../../api/api";
import { fecth_list_items } from "../../../../../actions/actions";
//Styles
import makeStyles from "./Third_Section_List_styles";

const Third_Section_List = ({ setting_boolean, set_Setting_bolean, item }) => {
	const styles = makeStyles();
	const [count, setCount] = useState(1);
	//When dealing with useSelector, use useEffect to update the state of a component
	useEffect(() => {
		setCount(item.count);
	}, [item]);

	//console.log("Count item: " + item.count);
	//console.log("Count State: " + count);

	const handle_threeDots_button = () => {
		set_Setting_bolean(!setting_boolean);
	};
	const handle_add = async () => {
		try {
			setCount(+count + 1); //This is stupid, prepend count with + to add intergers
			const updated_item = {
				listId: item.listId,
				userId: item.userId,
				name: item.name,
				count: count.toString(),
			};
			await update_Item(item.id, updated_item);
		} catch (error) {
			console.log(error.message);
		}
	};
	const handle_substract = async () => {
		try {
			setCount(+count - 1); //This is stupid, prepend count with + to add intergers
			const updated_item = {
				listId: item.listId,
				userId: item.userId,
				name: item.name,
				count: count.toString(),
			};
			await update_Item(item.id, updated_item);
		} catch (error) {
			console.log(error.message);
		}
	};
	return (
		<div>
			<div>
				{/* Three Dots button (Options) */}
				<Button className={styles.three_dots} onClick={handle_threeDots_button}>
					<MoreHorizIcon />
				</Button>
			</div>
			<div>
				{/* Item Count Buttons */}
				<ButtonGroup className={styles.group_button}>
					<Button className={styles.minus} onClick={handle_substract}>
						-
					</Button>
					<Button className={styles.count}>{count}</Button>
					<Button className={styles.plus} onClick={handle_add}>
						+
					</Button>
				</ButtonGroup>
			</div>
		</div>
	);
};

export default Third_Section_List;
