		<div>
			<div>
				<Add_Item />
			</div>
			<div>
				{edited_Item.map((item) => (
					<List item={item} />
				))}
			</div>
			<div>
				<Confirm set_isEditable={set_isEditable} />
			</div>
		</div>
	);
};

export default Edit_List;
