import React, { useEffect } from "react";

//Local imports
import Add_Item from "./Add_Item/Add_item";
import Confirm from "./Confirm/Confirm";
import List from "./List/List";

const Edit_List = ({ set_isEditable, items }) => {
	const editItem = items.map((item) => {
		item.isChecked = false;
		return item;
	});

	useEffect(() => {
		console.log(JSON.stringify(editItem));
	}, []);

	return (
		<div>
			<div>
				<Add_Item />
			</div>
			<div>
				{editItem.map((item) => (
					<List item={item} />
				))}
			</div>
			<div>
				<Confirm set_isEditable={set_isEditable} />
			</div>
		</div>
	);
};

export default Edit_List;
