import React from "react";
import { useEffect, useState } from "react";
//Redux
import { useSelector, useDispatch } from "react-redux";
//Material UI
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import ShareIcon from "@material-ui/icons/Share";
import AddIcon from "@material-ui/icons/Add";
//Components
import makeStyles from "../../styles/Lists";
import List from "./List";
//Actions
import { fecth_list_items } from "../../actions/actions";

const Lists = () => {
	const styles = makeStyles();
	const list_Name = useSelector((state) => state.homePage.name); //gets name of the list
	const user = useSelector((state) => state.login.profileObj.email); // gets the email of the user
	const [items, setItems] = useState(useSelector((state) => state.lists));
	const dispatch = useDispatch();

	//console.log(`List from Lists Component: ${JSON.stringify(list_Name)}`);
	//console.log(`Items from Lists Component: ${JSON.stringify(items)}`);

	useEffect(() => {
		dispatch(fecth_list_items(user, list_Name));
		return function cleanup() {
			setItems("");
		};
	}, [dispatch]);
	//======================================================================

	return (
		<>
			<div>
				<Grid container className={styles.user}>
					<Grid item xs={3}>
						<Typography class={styles.userName}> Hi Justin</Typography>
					</Grid>
					<Grid item xs={9} className={styles.buttons}>
						<Button
							fontSize="small"
							startIcon={<ShareIcon />}
							className={styles.topIconButton}
						>
							Share
						</Button>
						<Button
							fontSize="small"
							startIcon={<AddIcon />}
							className={styles.topIconButton2}
						>
							Delete List
						</Button>
					</Grid>
				</Grid>
			</div>
			<div>
				{items.map((item) => (
					<List item={item} />
				))}
			</div>
		</>
	);
};

export default Lists;
