import React from "react";
import { useEffect } from "react";
//Redux
import { useSelector, useDispatch } from "react-redux";
//Material UI
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import ShareIcon from "@material-ui/icons/Share";
import AddIcon from "@material-ui/icons/Add";
//Components
import makeStyles from "../../styles/Lists";
import List from "./List";
//Actions
import fecth_list_items from "../../actions/actions";

const Lists = () => {
	const styles = makeStyles();
	const list = useSelector((state) => state.homePage);
	const user = useSelector((state) => state.login.profileObj.email);
	const dispatch = useDispatch();

	useEffect(() => {
		dispatch(fetchLists(user, list.name));
	});
	//======================================================================

	return (
		<>
			<div>
				<Grid container className={styles.user}>
					<Grid item xs={3}>
						<Typography class={styles.userName}> Hi Justin</Typography>
					</Grid>
					<Grid item xs={9} className={styles.buttons}>
						<Button
							fontSize="small"
							startIcon={<ShareIcon />}
							className={styles.topIconButton}
						>
							Share
						</Button>
						<Button
							fontSize="small"
							startIcon={<AddIcon />}
							className={styles.topIconButton2}
						>
							Delete List
						</Button>
					</Grid>
				</Grid>
			</div>
			<div>
				<List list={list} />
			</div>
		</>
	);
};

export default Lists;
